              AND asterisk_log_calls_uniqueid NOT IN (
                SELECT asterisk_log_calls_uniqueid
                FROM asterisk_log_calls
                WHERE asterisk_log_calls_disposition = 'ANSWERED'
              )
            GROUP BY asterisk_log_calls_uniqueid
            ORDER BY asterisk_log_calls_uniqueid DESC
            ")->result_array();

