                        $chiamata_persa['asterisk_log_calls_callee_name'] ?: '',
                        $chiamata_persa['asterisk_log_calls_src']
                    ]);
                }

                $elenco_chiusure .= $this->table->generate() . '<hr/>';
            }

            $this->table->clear();
        }

        $mail_data = [
            'data_chiusura' => (new DateTime('yesterday'))->format('d/m/Y'),
            'elenco_chiamate_perse' => $elenco_chiusure,
        ];

