{"fi_events_type":"cron","cron":{"crons_id":"37","crons_type":"custom_code","crons_frequency":"5","crons_module":"customers","crons_active":"1","crons_cli":"0"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"echo_flush("[CRON CUSTOMER GEOCODING] Inizio<br/>");
log_message('debug', "[CRON CUSTOMER GEOCODING] Inizio");

$clienti = $this->db
    ->join('countries', 'countries_id = customers_country_id', 'LEFT')
    ->where("(customers_position IS NULL OR customers_position = '')", null, false)
    ->where("(customers_address IS NOT NULL AND customers_address <> '')", null, false)
    ->where("(customers_city IS NOT NULL AND customers_city <> '')", null, false)
    ->order_by('customers_modified_date', 'ASC')
    ->limit(1)
    ->get('customers')->result_array();

if (!empty($clienti)) {
    foreach ($clienti as $cliente) {

        $address = ucfirst(trim($cliente['customers_address']));
        $city = ucfirst(trim($cliente['customers_city']));
        $postalcode = ucfirst(trim($cliente['customers_zip_code']));
        $country = ucfirst(trim($cliente['countries_name']));

        echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Avvio ricerca con indirizzo, città e nazione<br/>");
        log_message('debug', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Avvio ricerca con indirizzo, città e nazione");

        $data = getReverseGeocoding("$address, $city, $country");

        if (empty($data[0])) {
            echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] NESSUN RISULTATO. Riprovo con solo città, cap e nazione<br/>");
            log_message('debug', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] NESSUN RISULTATO. Riprovo con solo città e nazione");

            $data = getReverseGeocoding("$city, $country");

            if (empty($data[0])) {
                echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] NESSUN RISULTATO. Riprovo con solo nazione<br/>");
                log_message('debug', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] NESSUN RISULTATO. Riprovo con solo nazione");

                $data = getReverseGeocoding("$country");
            }
        }

        if (empty($data[0])) {
            try {
                echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Aggiorno data modifica<br/>");
                log_message('debug', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Aggiorno data modifica");

                $this->apilib->edit('customers', $cliente['customers_id'], ['customers_modified_date' => date('Y-m-d H:i:s')]);
            } catch (Exception $e) {
                echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] ".$e->getMessage());
                log_message('error', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] ".$e->getMessage());
            }

            return false;
        }

        $lat_lng = $data[0]['lat'].';'.$data[0]['lon'];

        try {
            $this->apilib->edit('customers', $cliente['customers_id'], ['customers_position' => $lat_lng, 'customers_modified_date' => date('Y-m-d H:i:s')]);

            echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Impostato LAT-LNG<br/>");
            log_message('debug', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] Impostato LAT-LNG<br/>");
        } catch (Exception $e) {
            echo_flush("[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] ".$e->getMessage());
            log_message('error', "[CRON CUSTOMER GEOCODING {$cliente['customers_company']}] ".$e->getMessage());
        }
    }
} else {
    echo_flush("[CRON CUSTOMER GEOCODING] Nessun cliente da processare<br/>");
    log_message('debug', "[CRON CUSTOMER GEOCODING] Nessun cliente da processare");
}

echo_flush("[CRON CUSTOMER GEOCODING] Fine<br/>");
log_message('debug', "[CRON CUSTOMER GEOCODING] Fine");"},"fi_events_title":"[MODULO CUSTOMERS] Autogeocoding customers addresses","save":"0"}

