    $psw = $data['original_post']['customers_contacts_password'];

    // 08/08/2022 - Mando mail solo nel caso in cui si sia scelto di creare account per questo contatto
    if($data['post']['customers_contacts_send_credential_by_email'] == DB_BOOL_TRUE && !empty($email) && !empty($psw) && $data['post']['customers_contacts_create_access'] == DB_BOOL_TRUE) {
        //invio mail con credenziali
        $name = $data['post']['customers_contacts_name'];
        $last_name = $data['post']['customers_contacts_last_name'] ?? '';

        //invia mail con credenziali
        $mail_data['name'] = $name;
