        $mail_data['email'] = $email;
        $mail_data['psw'] = $psw;
        $mail_data['link'] = base_url();

        $this->mail_model->send($email, 'new_customer_contact', 'it', $mail_data);
    }
}"},"fi_events_title":"[MODULE CUSTOMERS] - Send email to customer contacts ","save":"1"}

