{"_when":"","fi_events_type":"database","pp":{"post_process_id":"343","post_process_when":"save","post_process_entity_id":"10","post_process_module":"customers","post_process_crm":"1","post_process_api":"1","post_process_apilib":"1","post_process_background":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"// Se c'è un modulo asterisk installato, per ogni contatto creo anche il record in rubrica per mantenere tutte le funzionalità legate
if ($this->datab->module_installed('asterisk-logs')) {

    // Prepare contact array
    $contact['rubrica_cliente'] = $data['customers_contacts_customer_id'];
    $contact['rubrica_nome'] = $data['customers_contacts_name'];
    $contact['rubrica_cognome'] = $data['customers_contacts_last_name'];
    $contact['rubrica_email'] = $data['customers_contacts_email'];
    $contact['rubrica_cliente_contatto'] = $data['customers_contacts_id'];

    // Check if rubrica contacts exist with phone number
    if ($data['customers_contacts_phone']) {
        $check_telephone = $this->db->where('rubrica_recapito_telefonico', $data['customers_contacts_phone'])->get('rubrica');

        $contact['rubrica_recapito_telefonico'] = $data['customers_contacts_phone'];

        // If exists, update
        if ($check_telephone->num_rows() > 0) {
            foreach ($check_telephone->result_array() as $rubrica) {
                $this->apilib->edit('rubrica', $rubrica['rubrica_id'], $contact);
            }
        } else {
            // If doesn't exist, create
            $rubrica = $this->apilib->create('rubrica', $contact);
        }
        // vedo se ci sono chiamate passate con quel numero
        $check_calls = $this->db->where('asterisk_log_calls_src', $data['customers_contacts_phone'])->get('asterisk_log_calls');
        // If exists, update
        if ($check_calls->num_rows() > 0) {
            foreach ($check_calls->result_array() as $call) {
                $this->apilib->edit('asterisk_log_calls', $call['asterisk_log_calls_id'], ['asterisk_log_calls_contact' => $rubrica['rubrica_id']]);
            }
        }
    }

    // Check if rubrica contacts exist with mobile number
    if ($data['customers_contacts_mobile_number']) {
        $check_telephone = $this->db->where('rubrica_recapito_telefonico', $data['customers_contacts_mobile_number'])->get('rubrica');

        $contact['rubrica_recapito_telefonico'] = $data['customers_contacts_mobile_number'];

        // If exists, update
        if ($check_telephone->num_rows() > 0) {
            foreach ($check_telephone->result_array() as $rubrica) {
                $output = $this->apilib->edit('rubrica', $rubrica['rubrica_id'], $contact);
            }
        } else {
            // If doesn't exist, create
            $rubrica = $this->apilib->create('rubrica', $contact);
        }
        // vedo se ci sono chiamate passate con quel numero
        $check_calls = $this->db->where('asterisk_log_calls_src', $data['customers_contacts_mobile_number'])->get('asterisk_log_calls');
        // If exists, update
        if ($check_calls->num_rows() > 0) {
            foreach ($check_calls->result_array() as $call) {
                $this->apilib->edit('asterisk_log_calls', $call['asterisk_log_calls_id'], ['asterisk_log_calls_contact' => $rubrica['rubrica_id']]);
            }
        }

    }

}"},"fi_events_title":"Creazione rubrica per asterisk","save":"1"}

