        if($settings['mail_up_sync_settings_automatic_sync'] == DB_BOOL_TRUE):
        $cliente = $this->apilib->searchFirst('customers', ['customers_id' => $data['post']['customers_id']]);

        $customers_tag_attuali = $cliente['customers_tags'];
        if (isset($data['original_post']['customers_tags'])) {
            $customers_tag_nuovi = $data['original_post']['customers_tags'];
        } else {
            $customers_tag_nuovi = [];
        }
        if (is_array($customers_tag_attuali)) {
            $customers_tag_attuali = array_keys($customers_tag_attuali);
        } else {
            $customers_tag_attuali = [];
        }

        $rimozioni = array_diff($customers_tag_attuali, $customers_tag_nuovi);
        $aggiunte = array_diff($customers_tag_nuovi, $customers_tag_attuali);
        $this->load->model('contacts-mailup-sync/mail_up_sync');
        if(!empty($aggiunte)){
            $this->mail_up_sync->modifica_customer($aggiunte,$data['post']['customers_id'],'aggiunta');
        }
        if(!empty($rimozioni)){
            $this->mail_up_sync->modifica_customer($rimozioni,$data['post']['customers_id'],'rimozione');
        }
        endif;
    endif;
