{"_when":"pre","fi_events_type":"database","pp":{"post_process_id":"551","post_process_when":"insert","post_process_entity_id":"513","post_process_module":"documents","post_process_crm":"1","post_process_api":"1","post_process_apilib":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"if (!empty($data['post']['documents_protocollo']) && $data['post']['documents_protocollo'] == DB_BOOL_TRUE) {    
    $azienda = $data['post']['documents_azienda'];

    if (!empty($azienda)) {
        $where_azienda = "AND documents_azienda = '{$azienda}'";
    } else {
        $where_azienda = "AND (documents_azienda IS NULL OR documents_azienda = '')";
    }

    $max = $this->db->query("SELECT MAX(CAST(documents_code AS UNSIGNED)) AS c, documents_code FROM documents WHERE documents_protocollo = '1' {$where_azienda}")->row();

    $anno_corrente = date('Y'); // Ottieni l'anno corrente
    $anno_in_codice = explode('-', $max->documents_code)[1] ?? null; // Estrai l'anno dalla stringa

    if (!empty($max->c) && $anno_in_codice == $anno_corrente) {
        // Se l'anno dell'ultimo numero progressivo è lo stesso dell'anno corrente, incrementa il numero esistente
        $codice_successivo = ($max->c + 1) . '-' . $anno_corrente;
    } else {
        // Altrimenti, inizia un nuovo numero progressivo con l'anno corrente
        $codice_successivo = "1-{$anno_corrente}";
    }

    $data['post']['documents_code'] = $codice_successivo;

    if (!empty($data['post']['documents_template_protocollo'])) {
        $this->load->model('documents/pdf');

        $dst_dir = FCPATH . 'uploads/modules_files/documents';

        @mkdir($dst_dir);

        $file_path = $this->pdf->generate($data['post'], $dst_dir . '/doc_protocollo_' . $codice_successivo . '.pdf');

        if ($file_path != false) {
            $data['post']['documents_file'] = str_ireplace(FCPATH . 'uploads/', '', $file_path);
        }
    }
}"},"fi_events_title":"[DOCUMENTS MANAGER] Genero codice se documento protocollo","save":"1"}

