{"_when":"post","fi_events_type":"hook","hook":{"hooks_id":"329","hooks_type":"form","hooks_ref":"1719","hooks_module":"firemail","hooks_order":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"<script>
    var pullLeftDiv = document.createElement("div");
    pullLeftDiv.className = "pull-left";

    var buttonElement = document.createElement("button");
    buttonElement.innerHTML = "Testa connessione";
    buttonElement.className = "btn btn-primary btn-test-connessione";
    buttonElement.addEventListener("click", testConnessione); // Aggiungi un ascoltatore per il click
    pullLeftDiv.appendChild(buttonElement);

    var formActionsDiv = document.querySelector('.modal-content .form-actions');
    formActionsDiv.appendChild(pullLeftDiv);

    // Funzione per controllare lo stato dei campi di input e abilitare/disabilitare il pulsante di test
    function checkFields() {
        var email = document.querySelector('input[name="mailbox_accounts_email"]').value;
        var password = document.querySelector('input[name="mailbox_accounts_password"]').value;
        var server = document.querySelector('input[name="mailbox_accounts_server"]').value;
        var port = document.querySelector('input[name="mailbox_accounts_port"]').value;

        if (email && password && server && port) {
            buttonElement.removeAttribute("disabled"); // Abilita il pulsante se tutti i campi sono popolati
        } else {
            buttonElement.setAttribute("disabled", "disabled"); // Disabilita il pulsante se almeno un campo è vuoto
        }
    }

    // Aggiungi un ascoltatore di evento per controllare lo stato dei campi di input ogni volta che vengono modificati
    // Aggiungi un ascoltatore di evento per controllare lo stato dei campi di input ogni volta che vengono modificati
    document.querySelectorAll('input[name^="mailbox_accounts_"], select[name="mailbox_accounts_encryption"]').forEach(function(input) {
        input.addEventListener("input", function() {
            checkFields(); // Controlla lo stato dei campi di input per abilitare/disabilitare il pulsante
            resetButton(); // Ripristina il pulsante "Testa connessione" e il suo stile
        });
    });

    // Funzione per ripristinare il pulsante "Testa connessione" e il suo stile
    function resetButton() {
        var buttonElement = document.querySelector('.btn-test-connessione');
        buttonElement.innerHTML = "Testa connessione";
        buttonElement.classList.remove("btn-danger");
        buttonElement.classList.remove("btn-success");
        buttonElement.classList.add("btn-primary");
    }

    // Controlla lo stato dei campi di input all'avvio per abilitare/disabilitare il pulsante
    checkFields();

    function testConnessione(event) {
        event.preventDefault(); // Impedisce il comportamento predefinito del pulsante

        var email = document.querySelector('input[name="mailbox_accounts_email"]').value;
        var password = document.querySelector('input[name="mailbox_accounts_password"]').value;
        var server = document.querySelector('input[name="mailbox_accounts_server"]').value;
        var port = document.querySelector('input[name="mailbox_accounts_port"]').value;
        var encryption = document.querySelector('select[name="mailbox_accounts_encryption"]').value;
        var inputs_validate = document.querySelectorAll('input[name="mailbox_accounts_validate_certificate"]');
        var validate;

        inputs_validate.forEach(function(input) {
            if (input.checked) {
                validate = input.value;
            }
        });


        request(base_url + 'firemail/testConnection', {
            "email": email,
            "password": password,
            "server": server,
            "port": port,
            "encryption": encryption,
            "validate": validate,
            [token_name]: token_hash
        }, 'POST', false, false, {}).then(data => {
            var buttonElement = document.querySelector('.btn-test-connessione');
            if (!data.success) {
                buttonElement.classList.remove("btn-primary");
                buttonElement.classList.add("btn-danger");
                buttonElement.innerHTML = "Errore, dati errati";
                setTimeout(function() {
                    buttonElement.innerHTML = "Testa connessione";
                    buttonElement.classList.remove("btn-danger");
                    buttonElement.classList.add("btn-primary");

                }, 5000); // 5 secondi
            }

            if (data.success) {
                buttonElement.classList.remove("btn-danger");
                buttonElement.classList.remove("btn-primary");
                buttonElement.classList.add("btn-success");
                buttonElement.innerHTML = "Dati corretti";
                setTimeout(function() {
                    buttonElement.innerHTML = "Testa connessione";
                    buttonElement.classList.remove("btn-success");
                    buttonElement.classList.add("btn-primary");

                }, 5000); // 5 secondi
            }
        }).catch(error => {
            console.log(error);
        });
    }
</script>"},"fi_events_title":"[MODULE IMAP WEBMAIL] - Test connection","save":"1"}

