        var $form = $('#genera_documento_under_stock');

        var $js_bulk_action = $('.js-bulk-action', layout_box_container);

        $js_bulk_action.append('<option value="genera_documento_from_under_stock">Genera documento</option>');

        $js_bulk_action.on('change', function () {
            var chkbx_ids = $("input:checkbox.js_bulk_check:checked", grid_container).map(function () {
                return $(this).val();
            }).get();

            if (chkbx_ids.length <= 0) {
                $("input:checkbox.js_bulk_check:checked", grid_container).val('').trigger('change');

                return false;
