                          value: chkbx_id
                        }).appendTo($form);
                    });

                    let tipi_doc_arr = {};

                    tipi_doc_obj.forEach(tipo_doc => {
                        tipi_doc_arr[tipo_doc.documenti_contabilita_tipo_id] = tipo_doc.documenti_contabilita_tipo_value
                    });

