<script>
    $(function() {
        var $form = $('.form_mailer_tempalte');        
        var edit_id = $form.data('edit-id');

        var $template_module = $('[name="mailer_template_module"]', $form);
        var modules = <?php echo json_encode($modules); ?>;

        var _template = null;

        if (edit_id) {
            $.ajax({
                url: base_url + 'get_ajax/getjsonrecord/mailer_template/' + edit_id,
                async: false,
                dataType: 'json',
                success: function(response) {
                    _template = response.data;
                }
            });
        }

        $.each(modules, function(index, module) {
            var selected = '';
            if (edit_id && module.modules_identifier == _template.mailer_template_module) {
                selected = 'selected="selected"';
            }

            $template_module.append('<option value="'+module.modules_identifier+'" '+selected+'>'+module.modules_name+'</option>');
        });
    });
</script>"},"fi_events_title":"[MAILER] Hook form new/edit template","save":"0"}

