                            <script>

                            function getAjax(url, callback){
                                var xmlhttp;
                                // compatible with IE7+, Firefox, Chrome, Opera, Safari
                                xmlhttp = new XMLHttpRequest();
                                xmlhttp.onreadystatechange = function() {
                                    if (xmlhttp.readyState == 4 && xmlhttp.status == 200){
                                     
                                        if (typeof callback === "function") {
                                            callback.apply(xmlhttp.responseText);
                                        }
                                    }
                                }
                                xmlhttp.open("GET", url, true);
                                xmlhttp.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
                                xmlhttp.send();
                            }

                            document.getElementById('js_spedisci_lettera').addEventListener('click', function(e){

                                getAjax(base_url + "openapi-integration/main/spedisci_lettera/<?php echo $lettera['openapi_lettere_id'];?>", function () {
                                    console.log(this);
                                    var json = JSON.parse(this);
                                    if (json.success == false) {
                                        document.getElementById('js_output_request').innerHTML = "Errore: "+json.message;
                                    } else {
                                        document.getElementById('js_output_request').innerHTML = "Complimenti! L'invio  stato effettuato!";
                                         document.getElementById('js_spedisci_lettera').style.display = 'none';
                                    }
                                    
                                });
                                
                            });

                                  // Crea anagrafica
                                $('body').on('click', '#VVVVVV', function (e) {
                                    e.stopImmediatePropagation();

                                    $('#msg_form_cercapivaform').append('<br /><br />Attendere ...');

                                    $.ajax({
                                        method: 'post',
                                        async: true,
                                        url: base_url + "openapi-integration/main/crea_anagrafica",
                                        dataType: "json",
                                        data: {
                                            data: ricerca_data,
                                            [token_name]: token_hash
                                        },
                                        success: function (res) {
                                            console.log(res);
                                            if (res.success == false) {
                                                $('#msg_form_cercapivaform').html("Errore: " + res.message);
                                            } else {
                                                // Redirect to customer detail
                                                window.location.href = res.message;
                                            }
                                        }
                                    });
                                });
                            </script>