{"_when":"post","fi_events_type":"hook","hook":{"hooks_id":"199","hooks_type":"form","hooks_ref":"198","hooks_module":"payments-subscriptions","hooks_order":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"<script>
    $(function() {
        var product_field = $('.js_sub_product');
        var price_field = $('.js_sub_price');
        var quantity_field = $('.js_sub_quantity');
        var product_unit_price_field = $('.js_product_unit_price_field');

        product_field.on('change', function() {
            var $this = $(this);
            var selected = $this.find(':selected').val();
            var ref_entity = $this.data('ref');

            if (selected) {
                $.ajax({
                    url: base_url + 'get_ajax/getJsonRecord/'+ref_entity+'/'+selected,
                    type: 'post',
                    dataType: 'json',
                    data: {
                        [token_name]: token_hash,
                    },
                    async: false,
                    success: function (res) {
                    	if (res.status == '0' && res.data) {
                            var product = res.data;

                            $('[name="subscriptions_name"]').val(product.fw_products_name);

                            if (product.fw_products_sell_price) {
                                price_field.val(product.fw_products_sell_price);
                                product_unit_price_field.val(product.fw_products_sell_price);
                            }
                        }
                	},
                    error: function (status, request, error) {
                        console.log(error);
                    },
                });
            }
        });

        quantity_field.on('change', function() {
            var sub_qty = parseFloat($(this).val());
            var sub_unit_price = parseFloat(product_unit_price_field.val());
            var sub_price = sub_unit_price;

            if (sub_qty && $.isNumeric(sub_qty) && sub_qty > 0 && sub_unit_price && $.isNumeric(sub_unit_price) && sub_unit_price > 0) {
            	sub_price = sub_unit_price * sub_qty;
            }

            if (sub_price && sub_price > 0) {
                price_field.val(sub_price.toFixed(2));
            }
        }).trigger('change');
    })
</script>"},"fi_events_title":"SUBSCRIPTION get price on product change","save":"1"}

