                                dataType: 'json',
                                async: false,
                                data: {
                                    [token_name]: token_hash,
                                    ids: chkbx_ids,
                                    serie: choosen_serie,
                                    data_emissione: data_emissione,
                                    data_scadenza: data_scadenza,
                                },
                                success: function(res) {                                
                                    alert(res.txt);

                                    if (res.status == '0') {
                                        return false;
                                    }

                                    location.reload();
                                },
                                error: function(status, request, error) {}
                            });
                        }
                    });
                }
            }
        });
