{"_when":"pre","fi_events_type":"database","pp":{"post_process_id":"310","post_process_when":"insert","post_process_entity_id":"182","post_process_module":"plan","post_process_crm":"1","post_process_api":"1","post_process_apilib":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"$impostazioni_rapportini = $this->apilib->searchFirst('impostazioni_rapportini');
$controllo_orari = $impostazioni_rapportini['impostazioni_rapportini_controllo_orari'] ?? DB_BOOL_FALSE;

if(strtotime("today") < strtotime($data['post']['rapportini_data'])) {
    throw new ApiException("Non puoi creare un rapportino per una data futura");
    exit;
}

if(!empty($data['post']['rapportini_data']) && !empty($data['post']['rapportini_ora_inizio']) && !empty($data['post']['rapportini_ora_fine'])) {
    if($controllo_orari == DB_BOOL_TRUE) {
        if($data['post']['rapportini_ora_inizio'] > $data['post']['rapportini_ora_fine']) {
            throw new ApiException("L'ora di inizio deve essere precedente all'ora di fine");
            exit;
        }   
    }

    //Se ore totali rapportino <> ore totali previste sull'appuntamento non devo creare presenza
    if(!empty($data['post']['rapportini_appuntamento_id'])) {
        $appuntamento = $this->apilib->view('appuntamenti', $data['post']['rapportini_appuntamento_id']);

        //Ore totali appuntamento
        $inizio_appuntamento = new DateTime(str_ireplace(' 00:00:00', '', $appuntamento['appuntamenti_giorno']).' '.$appuntamento['appuntamenti_ora_inizio']);
        $fine_appuntamento = new DateTime(str_ireplace(' 00:00:00', '', $appuntamento['appuntamenti_giorno']).' '.$appuntamento['appuntamenti_ora_fine']);
        $diff_appuntamento = $fine_appuntamento->diff($inizio_appuntamento);
        $hours_appuntamento = round(($diff_appuntamento->s / 3600) + ($diff_appuntamento->i / 60) + $diff_appuntamento->h + ($diff_appuntamento->days * 24), 2);
        //dump($hours_appuntamento);

        //Ore totali rapportino
        $inizio_rapportino = new DateTime($data['post']['rapportini_data'].' '.$data['post']['rapportini_ora_inizio']);
        $fine_rapportino = new DateTime($data['post']['rapportini_data'].' '.$data['post']['rapportini_ora_fine']);
        $diff_rapportino = $fine_rapportino->diff($inizio_rapportino);
        $hours_rapportino = round(($diff_rapportino->s / 3600) + ($diff_rapportino->i / 60) + $diff_rapportino->h + ($diff_rapportino->days * 24), 2);
        //dump($hours_rapportino);

        //Imposto rapportino come da validare
        if($hours_rapportino != $hours_appuntamento) {
            $data['post']['rapportini_da_validare'] = DB_BOOL_TRUE;
        }
    }

    // I rapportini liberi (non legati ad un appuntamento quindi creati dalla pagina Rapportini) devono essere sempre da approvare
    if(empty($data['post']['rapportini_appuntamento_id'])) {
        $data['post']['rapportini_da_validare'] = DB_BOOL_TRUE;
    }

    //Se sto creando un rapportino libero e ce n'è già uno per la data ed orari che sto inserendo devo bloccare la creazione
    if(empty($data['post']['rapportini_appuntamento_id'])) {
        foreach ($data['original_post']['rapportini_operatori'] as $user_id => $user) {
            //Per ogni operatore selezionato vedo se c'è già un rapportino nell'intervallo specificato
            $ora_inizio = $data['post']['rapportini_ora_inizio'];
            $ora_fine = $data['post']['rapportini_ora_fine'];

            $rapportini_esistenti = $this->db->query("
        	SELECT * FROM rapportini WHERE rapportini_id IN 
            	(SELECT rapportini_id FROM rel_rapportini_users WHERE users_id = '{$user}')
            AND rapportini_data = '{$data['post']['rapportini_data']}'
            AND (
                (rapportini_ora_inizio <= '{$ora_inizio}' AND rapportini_ora_fine >= '{$ora_fine}') OR
                (rapportini_ora_inizio >= '{$ora_inizio}' AND rapportini_ora_fine <= '{$ora_fine}') OR
                (rapportini_ora_inizio <= '{$ora_inizio}' AND rapportini_ora_fine >= '{$ora_inizio}') OR
                (rapportini_ora_inizio <= '{$ora_fine}' AND rapportini_ora_fine >= '{$ora_fine}')
            )
		")->result_array();
            //dump($rapportini_esistenti);
            if(!empty($rapportini_esistenti)) {
                throw new ApiException("Hai già inserito una rapportino nell'intervallo di tempo specificato.");
                exit;
            }
        }
    }

    //Se il rapportino è da validare mando notifica crm
    if($data['post']['rapportini_da_validare'] == DB_BOOL_TRUE) {
        $dip_user_id = $data['original_post']['rapportini_operatori'][0];

        $dipendente = $this->apilib->searchFirst('dipendenti', ['dipendenti_user_id' => $dip_user_id]);
        $impostazioni = $this->apilib->searchFirst('impostazioni_rapportini');
        $commessa = $this->apilib->view('projects', $data['post']['rapportini_commessa']);

        if(!empty($dipendente) && !empty($impostazioni) && !empty($impostazioni['impostazioni_rapportini_email_notifiche'])) {
            $dip = $dipendente['dipendenti_nome'].' '.$dipendente['dipendenti_cognome'];

            $mail_data['dipendente'] = $dip;
            $mail_data['commessa'] = $commessa['projects_name'];
            $mail_data['data'] = dateFormat($data['post']['rapportini_data'], 'd/m/Y');
            $mail_data['ora_inizio'] = $data['post']['rapportini_ora_inizio'];
            $mail_data['ora_fine'] = $data['post']['rapportini_ora_fine'];
            $mail_data['note'] = $data['post']['rapportini_note'] ?? '-';
            $mail_data['link'] = base_url('main/layout/dashboard_controllo');

            $this->mail_model->send($impostazioni['impostazioni_rapportini_email_notifiche'], 'rapportino_da_validare', 'it', $mail_data);
            /*$this->db->insert('notifications', [
                'notifications_type' => 5,
                'notifications_user_id' => 1,
                'notifications_title' => "Nuovo rapportino da validare",
                'notifications_message' => $dip." ha creato un rapportino da validare per la commessa ".$commessa['projects_name'].'<br/> Accedi alla dashboard di controllo per visualizzarlo.',
                'notifications_link' => "main/layout/653"
            ]);*/
        }
    }

}"},"fi_events_title":"Segno rapportino come da validare se le ore lavorate non combaciano con quelle previste dall'appuntamento","save":"1"}

