{"_when":"","fi_events_type":"database","pp":{"post_process_id":"311","post_process_when":"save","post_process_entity_id":"182","post_process_module":"plan","post_process_crm":"1","post_process_api":"1","post_process_apilib":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"//Se rapportino è NON "da_validare" creo la presenza relativa al rapportino appena inserito
//Quindi o perchè le ore lavorate sono minori/maggiori di quelle previste o perchè lo approvo manualmente
$rapportino = $this->apilib->view('rapportini', $data['rapportini_id']);

if($data['rapportini_da_validare'] == DB_BOOL_FALSE && !empty($rapportino['rapportini_operatori'])) {
    foreach ($rapportino['rapportini_operatori'] as $user_id => $user) {
        $dipendente = $this->apilib->searchFirst('dipendenti', ['dipendenti_user_id' => $user_id]);

        //Creo rapportino solo se sul dipendente è consentito farlo
        if(!empty($dipendente) && !empty($dipendente['dipendenti_timbra_da_rapportino']) && $dipendente['dipendenti_timbra_da_rapportino'] == DB_BOOL_TRUE) {
            try {
                //Ore totali rapportino
                $inizio_rapportino = new DateTime($data['rapportini_data'].' '.$data['rapportini_ora_inizio']);
                $fine_rapportino = new DateTime($data['rapportini_data'].' '.$data['rapportini_ora_fine']);
                $diff_rapportino = $fine_rapportino->diff($inizio_rapportino);
                $ore_totali = round(($diff_rapportino->s / 3600) + ($diff_rapportino->i / 60) + $diff_rapportino->h + ($diff_rapportino->days * 24), 2);

                //Calcolo ore appuntamento
                if(!empty($data['rapportini_appuntamento_id'])) {
                    $appuntamento = $this->apilib->view('appuntamenti', $data['rapportini_appuntamento_id']);
                    $inizio_appuntamento = new DateTime(str_ireplace(' 00:00:00', '', $appuntamento['appuntamenti_giorno']).' '.$appuntamento['appuntamenti_ora_inizio']);
                    $fine_appuntamento = new DateTime(str_ireplace(' 00:00:00', '', $appuntamento['appuntamenti_giorno']).' '.$appuntamento['appuntamenti_ora_fine']);
                    $diff_appuntamento = $fine_appuntamento->diff($inizio_appuntamento);
                    $ore_appuntamento = round(($diff_appuntamento->s / 3600) + ($diff_appuntamento->i / 60) + $diff_appuntamento->h + ($diff_appuntamento->days * 24), 2);

                    //Calcolo straordinario
                    $straordinario = $ore_totali - $ore_appuntamento;
                    $ore_straordinario = $straordinario > 0 ? $straordinario : 0;
                } else {
                    $ore_straordinario = 0;
                }

                //Ore per calendario
                $ora_inizio_calendar = $data['rapportini_data'].' '.$data['rapportini_ora_inizio'].':00';
                $ora_fine_calendar = $data['rapportini_data'].' '.$data['rapportini_ora_fine'].':00';

                //Cerco presenza già creata per questo rapportino, se la trovo sono in modifica
                $presenza_rapportino = $this->apilib->searchFirst('presenze', [
                    'presenze_rapportino_id' => $data['rapportini_id'],
                    'presenze_dipendente' => $dipendente['dipendenti_id'],
                ]);

                if(empty($presenza_rapportino)) {
                    //Creazione rapportino e presenza per la prima volta
                    //Se c'è già quella creata dal timbra entrata/uscita prima di creare questa devo cancellarle la precedente
