            $mail_data['ora_fine'] = $data['rapportini_ora_fine'];
            $mail_data['note'] = $data['rapportini_note'] ?? '-';

            if(!empty($dipendente['dipendenti_email'])) {
                $this->mail_model->send($dipendente['dipendenti_email'], 'rapportino_approvato_dipendente', 'it', $mail_data);   
            }
        }
    }
}




