        $codice = $codice_exp[0];
        $anno = $codice_exp[1];

        // Se l'anno dell'ultimo numero progressivo è lo stesso dell'anno corrente, incrementa il numero esistente
        if ($anno == $anno_corrente) {
            $codice_successivo = $codice + 1 . '-' . $anno_corrente;
        } else {
            $codice_successivo = "1-{$anno_corrente}";
        }
    } else {
        // Altrimenti, inizia un nuovo numero progressivo con l'anno corrente
        $codice_successivo = "1-{$anno_corrente}";
    }

	$data['post']['rapportini_codice'] = $codice_successivo;
