        // Vedi Task #17382
        $("[name='appuntamenti_ora_inizio'], [name='appuntamenti_ora_fine']").on('change', function() {
            if(
                ($("[name='appuntamenti_ora_inizio']").val() && $("[name='appuntamenti_ora_inizio']").val().length != 0) 
                ||
                ($("[name='appuntamenti_ora_fine']").val() && $("[name='appuntamenti_ora_fine']").val().length != 0)
            ) {
                const ora_inizio = moment($("[name='appuntamenti_ora_inizio']").val(), "HH:mm");
                const ora_fine = moment($("[name='appuntamenti_ora_fine']").val(), "HH:mm");

                // Se l'ora di fine è prima dell'ora di inizio, aggiungi un giorno all'ora di fine
                if (ora_fine.isBefore(ora_inizio)) {
                    ora_fine.add(1, 'days');
                }

                var diff = moment.duration(ora_fine.diff(ora_inizio));
                var hours = parseInt(diff.asHours());
                var minutes = parseInt(diff.asMinutes()) - hours * 60;
                // Converti i minuti in formato decimale
                var minutes_decimal = minutes / 60;
                var qta = hours + minutes_decimal;

                quantita.val(qta.toFixed(2));
            }
        });

        quantita.on('change', function() {
            const ora_inizio = moment($("[name='appuntamenti_ora_inizio']").val(), "HH:mm");
            var ore_minuti = parseFloat($(this).val());
            var ore = parseInt(ore_minuti);
            var minuti = (ore_minuti-ore) * 60;
            /*console.log('ore_minuti:'+ore_minuti);
            console.log('ore:'+ore);
            console.log('minuti:'+minuti);*/

            var ora_fine = ora_inizio.add(ore, 'hours').add(minuti, 'minutes').format('HH:mm');
            $("[name='appuntamenti_ora_fine']").val(ora_fine);
        });

        $("[name='appuntamenti_ora_inizio']").trigger("change");


        /***************************************
        *
        * Ottengo dati commessa e calcolo tempo
        * in base al tempo per stanza
        *
        ****************************************/
        var fullData = null;
        var commessaStanze = false;
        var tempoStanza = null;
        var nCamereAppuntamento = $("[name='appuntamenti_n_camere']").val();
        var commessaSelezionata =  $("[name='appuntamenti_impianto']");

        function getDatiCommessa(commessaSelezionata) {
            const selected = commessaSelezionata.val();
            $.ajax({
                url: base_url + 'get_ajax/getJsonRecord/projects/'+selected,
                dataType: 'json',
                data: {
                    [token_name]: token_hash
                },
                async: false,
                success: function (data) {
                    //console.log(data);
                    if(data.status === 0 && data.data.length != 0) {
                        commessaStanze = data.data.projects_commessa_a_stanze;
                        tempoStanza = data.data.projects_tempo_prev_stanza;
                        //console.log('commessaStanze: ', commessaStanze);
                        //console.log('tempoStanza: ', tempoStanza);
                        const totTempoStanze = calcolaTempoAppuntamento(commessaStanze, tempoStanza, nCamereAppuntamento);
                        //console.log(totTempoStanze);
                        const totTempoStanzeMinuti = totTempoStanze * 60;

                        const ora_inizio = moment($("[name='appuntamenti_ora_inizio']").val(), "HH:mm");
                        var ora_fine_tmp = ora_inizio.add(totTempoStanzeMinuti, 'minutes').format('HH:mm');
                        $("[name='appuntamenti_ora_fine']").val(ora_fine_tmp);

                        // Trigger per calcolo n ore
                        $("[name='appuntamenti_ora_inizio'], [name='appuntamenti_ora_fine']").trigger('change');
                    }
                }
            });
        }

        function calcolaTempoAppuntamento(commessaStanze, tempoStanza, nCamereAppuntamento) {
            var calcolo = 0;
            /*console.log(nCamereAppuntamento);
            console.log(tempoStanza);
            console.log(nCamereAppuntamento * tempoStanza);*/
            if(commessaStanze && (nCamereAppuntamento != 0 && tempoStanza != 0)) {
                calcolo = parseFloat(nCamereAppuntamento * tempoStanza);
            }
            return calcolo;
        }

        // Al cambio commessa richiedo i dati e calcolo
        $("[name='appuntamenti_impianto']").on('change', function() {
            commessaSelezionata = $(this);
            getDatiCommessa(commessaSelezionata);
        });
        // Al cambio del n° di camere richiedo i dati e calcolo
        $("[name='appuntamenti_n_camere']").on('change', function() {
            nCamereAppuntamento = $(this).val();
            getDatiCommessa(commessaSelezionata);
        });  

    });
</script>"}
