    $(function(){
        const quantita = $("[name='pianificazione_lavori_quantita']");

        $("[name='pianificazione_lavori_ora_inizio'], [name='pianificazione_lavori_ora_fine']").on('change', function() {
            const ora_inizio = moment($("[name='pianificazione_lavori_ora_inizio']").val(), "HH:mm");
            const ora_fine = moment($("[name='pianificazione_lavori_ora_fine']").val(), "HH:mm");

            var diff = moment.duration(ora_fine.diff(ora_inizio));
            var hours = parseInt(diff.asHours());
