{"_when":"post","fi_events_type":"hook","hook":{"hooks_id":"345","hooks_type":"form","hooks_ref":"2354","hooks_module":"planner-squadre","hooks_order":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"<script>
    $(function() {
        /**********************************************************
        *
        * 08/07/2024
        * Disabilito orari, ore tot e stanze se viene scelta
        * una fascia oraria compatibile con la data selezionata
        *
        ***********************************************************/
        // Al cambio commessa svuoto gli input degli orari ed ore
        $('[name="appuntamenti_impianto"]').change(function() {
            $('[name="appuntamenti_ora_inizio"]').val('');
            $('[name="appuntamenti_ora_fine"]').val('');
            $('[name="appuntamenti_n_ore"]').val('');
            $('[name="appuntamenti_n_camere"]').val('');

            enableInput();
        });

        //
        $('[name="appuntamenti_fascia_oraria"], [name="appuntamenti_giorno"]').change(function() {
            const selectedFascia = $('[name="appuntamenti_fascia_oraria"]').val();
            const selectedGiorno = $('[name="appuntamenti_giorno"]').val();

            if (selectedFascia && selectedGiorno) {
                const selectedDate = moment(selectedGiorno, 'DD/MM/YYYY');
                const dayOfWeek = selectedDate.isoWeekday(); // 1 LUN - 7 DOM
                // Prendo orari della fascia e li inserisco in ora inizio e fine, se sono in uno dei giorni che combaciano con la data scelta
                getOrariFascia(selectedFascia, selectedDate, dayOfWeek);
            } else {
                // Abilito input
                enableInput();
            }
        });

        function disableInput() {
            $('[name="appuntamenti_ora_inizio"]').prop('readonly', true);
            $('[name="appuntamenti_ora_fine"]').prop('readonly', true);
            $('[name="appuntamenti_n_ore"]').prop('readonly', true);
            $('[name="appuntamenti_n_camere"]').prop('readonly', true);
        }
        function enableInput() {
            $('[name="appuntamenti_ora_inizio"]').prop('readonly', false);
            $('[name="appuntamenti_ora_fine"]').prop('readonly', false);
            $('[name="appuntamenti_n_ore"]').prop('readonly', false);
            $('[name="appuntamenti_n_camere"]').prop('readonly', false);
        }

        // Calcola ore totali appuntamento
        function calcolaOreTotali() {
            const oraInizio = $('[name="appuntamenti_ora_inizio"]').val();
            const oraFine = $('[name="appuntamenti_ora_fine"]').val();

            if (oraInizio && oraFine) {
                const [inizioOre, inizioMinuti] = oraInizio.split(':').map(Number);
                const [fineOre, fineMinuti] = oraFine.split(':').map(Number);
                // Calcola la differenza totale in minuti
                const totaleMinuti = (fineOre * 60 + fineMinuti) - (inizioOre * 60 + inizioMinuti);

                if (totaleMinuti >= 0) { // Assicurati che l'ora di fine sia successiva all'ora di inizio
                    // Converti i minuti in ore decimali
                    const oreDecimali = (totaleMinuti / 60).toFixed(2);
                    // Imposta il risultato nell'input appuntamenti_n_ore
                    $('[name="appuntamenti_n_ore"]').val(oreDecimali);
                } else {
                    $('[name="appuntamenti_n_ore"]').val(''); // Resetta se l'orario non è valido
                }
            }
        }

        // Calcolo ore totali
        function calcolaOreTotali() {
            const oraInizio = $('[name="appuntamenti_ora_inizio"]').val();
            const oraFine = $('[name="appuntamenti_ora_fine"]').val();

            if (oraInizio && oraFine) {
                const [inizioOre, inizioMinuti] = oraInizio.split(':').map(Number);
                const [fineOre, fineMinuti] = oraFine.split(':').map(Number);

                // Diff. tot in minuti
                const minutiInizio = inizioOre * 60 + inizioMinuti;
                const minutiFine = fineOre * 60 + fineMinuti;
                // const totaleMinuti = (fineOre * 60 + fineMinuti) - (inizioOre * 60 + inizioMinuti);
                // Se l'ora di inizio è maggiore dell'ora di fine, consideriamo la fine il giorno successivo
                let totaleMinuti;
                if (minutiFine < minutiInizio) {
                    totaleMinuti = (1440 - minutiInizio) + minutiFine; // 1440 minuti in un giorno
                } else {
                    totaleMinuti = minutiFine - minutiInizio;
                }

                if (totaleMinuti >= 0) { // Assicurati che l'ora di fine sia successiva all'ora di inizio
                    // Converti i minuti in ore decimali
                    const oreDecimali = (totaleMinuti / 60).toFixed(2);
                    // Imposta il risultato nell'input appuntamenti_n_ore
                    $('[name="appuntamenti_n_ore"]').val(oreDecimali);
                } else {
                    $('[name="appuntamenti_n_ore"]').val(''); // Resetta se l'orario non è valido
                }
            }
        }

        function getOrariFascia(fasciaId, dataSelezionata, nGiornoSettimana) {
            if(fasciaId) {
                $.ajax({
                    url: base_url + 'get_ajax/getJsonRecord/projects_orari/'+fasciaId,
                    dataType: 'json',
                    data: {
                        [token_name]: token_hash
                    },
                    async: false,
                    success: function (data) {
                        if(data.status === 0 && data.data.length != 0) {
                            // Se sono in uno dei giorni applicabili disabilito input e inserisco orari
                            const isAllowed = data.data.projects_orari_giorni[nGiornoSettimana];
                            if(isAllowed) {
                                disableInput();

                                // Formattazione orari a due cifre per le ore
                                const [startHours, startMinutes] = data.data.projects_orari_dalle.split(':');
                                const formattedStartHours = startHours.padStart(2, '0');
                                const formattedStartMinutes = startMinutes.padStart(2, '0');
                                const oraInizio = `${formattedStartHours}:${formattedStartMinutes}`;

                                const [endHours, endMinutes] = data.data.projects_orari_alle.split(':');
                                const formattedEndHours = endHours.padStart(2, '0');
                                const formattedEndMinutes = endMinutes.padStart(2, '0');
                                const oraFine = `${formattedEndHours}:${formattedEndMinutes}`;

                                $("[name='appuntamenti_ora_inizio']").val(oraInizio);
                                $("[name='appuntamenti_ora_fine']").val(oraFine);
                                // Calcolo ore totali
                                calcolaOreTotali();

                                $("[name='appuntamenti_ora_inizio'], [name='appuntamenti_ora_fine']").trigger('change');
                            } else {
                                // Abilito input
                                enableInput();
                            }
                        }
                    }
                });  
            }
        }

        // Calcolo ore totali al cambio ora inizio e fine
        $("[name='appuntamenti_ora_inizio'], [name='appuntamenti_ora_fine']").change(function() {
            calcolaOreTotali();
        });


    });
</script>"},"fi_events_title":"[MODULO PLANNER SQUADRE - Appuntamenti] Compilazione orari in base alla fascia scelta","save":"1"}

