<script>
    $(function () {
        var grid_container = $('table.js_fg_grid_fw_products');
        var layout_box_container = grid_container.closest('div[data-layout-box]');

        var $form = $('#stampa_barcodes_prodotti');

        var $js_bulk_action = $('.js-bulk-action', layout_box_container);

        $js_bulk_action.append('<option value="action_bulk_print_pdf">Stampa PDF Barcodes</option>');
        $js_bulk_action.append('<option value="action_bulk_applica_sconto">Applica Sconto</option>');
        $js_bulk_action.append('<option value="action_bulk_assign_ean">(Ri)Assegna EAN</option>');
        $js_bulk_action.append('<option value="action_bulk_get_updates">Aggiorna da Woocommerce</option>');

        $js_bulk_action.on('change', function () {
            var chkbx_ids = $("input:checkbox.js_bulk_check:checked", grid_container).map(function () {
                return $(this).val();
            }).get();

            if (chkbx_ids.length <= 0) {
                $(".js-bulk-first-option", grid_container).trigger('change');

                return false;
            }

            if ($(this).val() === 'action_bulk_print_pdf') {
                $('#ids_bulk_print_pdf').val(JSON.stringify(chkbx_ids));
                $('#form_bulk_print_pdf').submit();
            }

            if ($(this).val() === 'action_bulk_assign_ean') {
                $('#ids_bulk_ean').val(JSON.stringify(chkbx_ids));
                $('#form_bulk_assign_ean').submit();
            }

            if ($(this).val() === 'action_bulk_applica_sconto') {
                var sconto_perc = prompt("Inserisci la percentuale numerica di sconto da applicare\n\nInserendo il valore 0, lo sconto verrà tolto.");

                var scontoNumerico = Number(sconto_perc);

                // Controlla se sconto_perc è un numero intero
                if (sconto_perc === null || sconto_perc.trim() === "" || isNaN(scontoNumerico) || !Number.isInteger(scontoNumerico)) {
                    alert("Devi inserire un valore numerico valido!");
                    return false;
                }

                $('#ids_bulk_scontistica_perc', $('#form_bulk_applica_sconto')).val(scontoNumerico);
                $('#ids_bulk_scontistica_prodotti', $('#form_bulk_applica_sconto')).val(JSON.stringify(chkbx_ids));

                $('#form_bulk_applica_sconto').submit();
            }

            if ($(this).val() === 'action_bulk_get_updates') {
                if (confirm('Vuoi sincronizzare le immagini?\n\n(Le attuali immagini verranno totalmente ri-scaricate e sovrascritte)')) {
                    $('#sync_images_bulk_get_updates').val('1').trigger('change');
                }

                $('#ids_bulk_get_updates').val(JSON.stringify(chkbx_ids));
                $('#form_bulk_get_updates').submit();
            }
        });
    });
</script>
