$oggi = date('Y-m-d');
//if($ora >= '08:59' && $ora <= '09:09' || $this->input->get('debug') === '1')
if($ora >= '06:39' && $ora <= '06:49' || $this->input->get('debug') === '1')
{
    //$elenco_utenti = $this->apilib->search('users', ['users_deleted' => 0]);
    $elenco_utenti = $this->db->query("SELECT * FROM users LEFT JOIN fi_todo_lists_users_mail ON users.users_id = fi_todo_lists_users_mail.fi_todo_lists_users_mail_user WHERE fi_todo_lists_users_mail.fi_todo_lists_users_mail_active = 1")->result_array();
    foreach($elenco_utenti as $utente){
        $lista_to_do = '';
        $elencotodo = $this->apilib->search('fi_todo', ['fi_todo_assigned_to' => $utente['users_id'],'fi_todo_deleted' => 0,'fi_todo_done' => 0,'fi_todo_deadline' => $oggi]);
        if ($elencotodo) {
            $lista_to_do .= '<ul style="margin-left: 0px; padding-left:0px;">';
            foreach($elencotodo as $key => $value){
                $lista_to_do .= '<li list-style-type:square; style="margin-left: 0px;"><strong>'.$value['fi_todo_text'].'</strong></li>';
        	}
            $lista_to_do .= '</ul>';
            $mail_data['lista_to_do'] = $lista_to_do;
            $mail_data['users'] = $utente['users_first_name'];
            $this->mail_model->send($utente['users_email'], 'scadenza_todo', '', $mail_data);
        }  
    }   
} else {
    echo "L'ora attuale non è tra le 08:59 e le 09:09";
}"},"fi_events_title":"Scadenze giornaliere","save":"1"}

