    $user_manager = $this->db->get('users_manager_configurations')->row();
    if (defined('PASSEPARTOUT')) {
        $this->PASSEPARTOUT = PASSEPARTOUT;
    }
    if(!isset($data['password_form'])){
        $data['password_form'] = '';
    }

    if (!empty($user_manager->users_manager_configurations_enable_password_expire) && $user_manager->users_manager_configurations_enable_password_expire == DB_BOOL_TRUE) {
        $password_expire_days = $user_manager->users_manager_configurations_password_expire_days;
        $days = 0;
        if (!empty($data[LOGIN_ENTITY.'_last_password_change'])) {
            $earlier = new DateTime($data[LOGIN_ENTITY.'_last_password_change']);
            $later = new DateTime();

            $days = $later->diff($earlier)->format("%a");
        }

        debug($days, true);

        if (empty($data[LOGIN_ENTITY.'_last_password_change']) || $days >= $password_expire_days) {
            $this->auth->logout();
            //debug($_SESSION,true);
            $this->session->set_userdata('change_password_email', $data[LOGIN_USERNAME_FIELD]);

